mapper.select:
select A.CO_ID as CO_ID, A.APPR_MST_ID as APPR_MST_ID, B.APPR_DESC as APPR_DESC, A.RCPT_INDC as RCPT_INDC, A.RFNC_INDC as RFNC_INDC, A.CPRTN_INDC as CPRTN_INDC, A.CPRTN_TYPE as CPRTN_TYPE, A.CRCLTN_INDC as CRCLTN_INDC, A.DCSN_RULE_INDC as DCSN_RULE_INDC, A.CRT_ID as CRT_ID, A.CREATE_DATE as CREATE_DATE, A.CHNG_ID as CHNG_ID, C.APPR_LEVEL as APPR_LEVEL, A.CHNG_DATE as CHNG_DATE, A.ATND_INDC as ATND_INDC, E.APPR_ITEM_NAME as APPR_TYPE, A.BASE_PRSV_TERM as BASE_PRSV_TERM from gw.nw010m A LEFT JOIN gw.nw011m B ON A.CO_ID = B.CO_ID AND A.APPR_MST_ID = B.APPR_MST_ID AND B.LOCALE = #{lang,jdbcType=VARCHAR} LEFT JOIN gw.nw012m C ON A.CO_ID = C.CO_ID AND A.APPR_MST_ID = C.APPR_MST_ID AND #{crntDate,jdbcType=VARCHAR} BETWEEN C.CRT_DATE AND C.CLS_DATE LEFT JOIN gw.nw015m D ON A.CO_ID = D.CO_ID AND A.APPR_MST_ID = D.APPR_MST_ID LEFT JOIN gw.nw014m E ON D.CO_ID = E.CO_ID AND D.APPR_ITEM_ID = E.APPR_ITEM_ID AND E.LOCALE = #{lang,jdbcType=VARCHAR}
A.CO_ID = #{coId,jdbcType=VARCHAR}
AND A.APPR_MST_ID LIKE #{searchWord,jdbcType=VARCHAR}
AND B.APPR_DESC LIKE #{searchWord,jdbcType=VARCHAR}
order by APPR_MST_ID
order by APPR_DESC

select  A.CO_ID as CO_ID, A.APPR_ITEM_ID as APPR_ITEM_ID, B.APPR_ITEM_NAME as APPR_ITEM_NAME,
		A.SEQ_NO as SEQ_NO, A.CREATE_DATE as CREATE_DATE, A.IMG_URL as IMG_URL
from    gw.nw013m A
    LEFT JOIN gw.nw014m B
        ON  A.CO_ID = B.CO_ID
	   AND  A.APPR_ITEM_ID = B.APPR_ITEM_ID
       AND  B.LOCALE = #{lang,jdbcType=VARCHAR}
where  A.CO_ID = #{coId,jdbcType=VARCHAR}
  and  A.APPR_ITEM_ID = #{apprItemId,jdbcType=VARCHAR}
