mapper.select:
 			
		select  A.CO_ID as CO_ID, A.MAJ_CODE as MAJ_CODE, A.RESC_KEY as RESC_KEY, B.RESC_KEY_VALUE as MAJ_CODE_NAME, 
		B.RESC_KEY_DESC as MAJ_CODE_DESC, A.USE_INDC as USE_INDC
		from   cm001c A 
		INNER JOIN cm003m B on (
		  	   A.CO_ID	=	#{coId,jdbcType=VARCHAR}
		  and  A.CO_ID	=	B.CO_ID
		  and  A.RESC_KEY = B.RESC_KEY
		  and  B.LOCALE = #{lang,jdbcType=VARCHAR}
		  
		  and  A.USE_INDC= #{useIndc,jdbcType=VARCHAR}
		  
		)
		order by A.CO_ID, A.MAJ_CODE

   
		select  A.CO_ID as CO_ID, A.MAJ_CODE as MAJ_CODE, A.RESC_KEY as RESC_KEY, B.RESC_KEY_VALUE as MAJ_CODE_NAME, 
		B.RESC_KEY_DESC as MAJ_CODE_DESC, A.USE_INDC as USE_INDC
		from   cm001c A 
		INNER JOIN cm003m B on (
		  	   A.CO_ID	=	#{coId,jdbcType=VARCHAR}
		  and  A.CO_ID	=	B.CO_ID
		  and  A.MAJ_CODE	=	#{majCode,jdbcType=VARCHAR}
		  and  A.RESC_KEY = B.RESC_KEY
		  and  B.LOCALE = #{lang,jdbcType=VARCHAR}		  
		)

mapper.update:
update cm001c
RESC_KEY= #{rescKey,jdbcType=VARCHAR}, MAJ_CODE_NAME= #{majCodeName,jdbcType=VARCHAR}, MAJ_CODE_DESC= #{majCodeDesc,jdbcType=VARCHAR}, USE_INDC= #{useIndc,jdbcType=VARCHAR},
where CO_ID = #{coId,jdbcType=VARCHAR} and MAJ_CODE = #{majCode,jdbcType=VARCHAR}

mapper.delete:
 
		delete	from cm001c 
		where	CO_ID	=	#{coId,jdbcType=VARCHAR}
		  and   MAJ_CODE	=	#{majCode,jdbcType=VARCHAR}
