mapper.select:
 
		select  CO_ID, EMP_NO, LOCALE, EMP_NAME
		from   hr011m
		where  CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and  EMP_NO	=	#{empNo,jdbcType=VARCHAR} 
		  and  LOCALE	=	#{locale,jdbcType=VARCHAR}

mapper.update:
 
		update	hr011m 
		 
			EMP_NAME= #{empName,jdbcType=VARCHAR}, 
		 
		where	CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and	EMP_NO	=	#{empNo,jdbcType=VARCHAR} 
		  and	LOCALE	=	#{locale,jdbcType=VARCHAR}

mapper.delete:
 
		delete	from hr011m 
		where	CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and	EMP_NO	=	#{empNo,jdbcType=VARCHAR} 
		  
		  and	LOCALE	=	#{locale,jdbcType=VARCHAR}
