mapper.select:
 
		select  CNTN_ID, CO_ID, ST_DATE, FIN_DATE, USE_INDC
		from   nw032m
		where  CNTN_ID	=	#{cntnId,jdbcType=VARCHAR} 
		  and  CO_ID	=	#{coId,jdbcType=VARCHAR}

mapper.update:
 
		update	nw032m 
		 
			ST_DATE= #{stDate,jdbcType=TIMESTAMP}, 
			FIN_DATE= #{finDate,jdbcType=TIMESTAMP},
			USE_INDC= #{useIndc,jdbcType=VARCHAR},  
		 
		where	CNTN_ID	=	#{cntnId,jdbcType=VARCHAR} 
		  and	CO_ID	=	#{coId,jdbcType=VARCHAR}

mapper.delete:
 
		delete	from nw032m 
		where	CNTN_ID	=	#{cntnId,jdbcType=VARCHAR}
		 
		  and	CO_ID	=	#{coId,jdbcType=VARCHAR}
