mapper.select:
 
		select  CO_ID, USER_ID, PSWD, PSWD_CHNG_DATE, PSWD_INIT_INDC, INTERNAL_MAIL_ADDR, OUTSIDE_MAIL_ADDR, CNFM_PSWD, CNFM_PSWD_CHNG_DATE, 
				USER_RGSR_DATE, USE_INDC
		from   nw100m
		where  CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and  USER_ID	=	#{userId,jdbcType=VARCHAR}

mapper.update:
update nw100m
PSWD= #{pswd,jdbcType=VARCHAR}, PSWD_CHNG_DATE= now(), PSWD_INIT_INDC= #{pswdInitIndc,jdbcType=VARCHAR}, INTERNAL_MAIL_ADDR= #{internalMailAddr,jdbcType=VARCHAR}, OUTSIDE_MAIL_ADDR= #{outsideMailAddr,jdbcType=VARCHAR}, CNFM_PSWD= #{cnfmPswd,jdbcType=VARCHAR}, CNFM_PSWD_CHNG_DATE= now(), USER_RGSR_DATE= #{userRgsrDate,jdbcType=TIMESTAMP}, USE_INDC= #{useIndc,jdbcType=CHAR},
where CO_ID = #{coId,jdbcType=VARCHAR} and USER_ID = #{userId,jdbcType=VARCHAR}

mapper.delete:
 
		delete	from nw100m 
		where	CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and	USER_ID	=	#{userId,jdbcType=VARCHAR}
