mapper.select:
 
		select  CO_ID, USER_ID, ROLE, USE_INDC
		from   nw105m
		where  CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and  ROLE	=	#{role,jdbcType=VARCHAR} 
		  and  USER_ID	=	#{userId,jdbcType=VARCHAR}

mapper.update:
 
		update	nw105m 
		 
			USE_INDC= #{useIndc,jdbcType=SMALLINT}, 
		 
		where	CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and	USER_ID	=	#{userId,jdbcType=VARCHAR} 
		  and	ROLE	=	#{role,jdbcType=VARCHAR}

mapper.delete:
 
		delete	from nw105m 
		where	CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and	ROLE	=	#{role,jdbcType=VARCHAR} 
		  and	USER_ID	=	#{userId,jdbcType=VARCHAR}
