mapper.select:
 
		select  CO_ID, OGAN_CODE, OGAN_LEV, OGAN_ESTB_DATE, OGAN_NAME, RESC_KEY, OGAN_CLS_DATE, HIGH_OGAN_CODE, HIGH_OGAN_LEV, 
				OGAN_TYPE, OGAN_DESC
		from   om001m
		where  CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and  OGAN_CODE	=	#{oganCode,jdbcType=VARCHAR} 
		  and  OGAN_LEV	=	#{oganLev,jdbcType=VARCHAR}
		  and  OGAN_ESTB_DATE	=	#{oganEstbDate,jdbcType=TIMESTAMP}

mapper.update:
 
		update	om001m 
		 
			OGAN_NAME= #{oganName,jdbcType=VARCHAR}, 
			RESC_KEY= #{rescKey,jdbcType=VARCHAR}, 
			OGAN_CLS_DATE= #{oganClsDate,jdbcType=TIMESTAMP}, 
			HIGH_OGAN_CODE= #{highOganCode,jdbcType=VARCHAR}, 
			HIGH_OGAN_LEV= #{highOganLev,jdbcType=VARCHAR}, 
			OGAN_TYPE= #{oganType,jdbcType=VARCHAR}, 
			OGAN_DESC= #{oganDesc,jdbcType=VARCHAR}, 
		 
		where	CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and	OGAN_CODE	=	#{oganCode,jdbcType=VARCHAR} 
		  and	OGAN_LEV	=	#{oganLev,jdbcType=VARCHAR} 
		  and	OGAN_ESTB_DATE	=	#{oganEstbDate,jdbcType=TIMESTAMP}

mapper.delete:
 
		delete	from om001m 
		where	CO_ID	=	#{coId,jdbcType=VARCHAR} 
		  and	OGAN_CODE	=	#{oganCode,jdbcType=VARCHAR} 
		  and	OGAN_ESTB_DATE	=	#{oganEstbDate,jdbcType=TIMESTAMP} 
		  and	OGAN_LEV	=	#{oganLev,jdbcType=VARCHAR}
